sp_widget.client_script:
function($scope, $location, $http, spModal, spUtil) {


	var c = this;
	c.debug = false;
	c.deleteRecord = deleteRecord;

	$scope.mandatory = [];
	$scope.pagination = [5, 10, 15, 20, 25];
	$scope.sortKey = null;
	$scope.reverse = null;

	$scope.sort = function(keyname){
		$scope.sortKey = keyname;   //set the sortKey to the param passed
		$scope.reverse = !$scope.reverse; //if true make it false and vice versa
	}

	$scope.exclude = function(key, value) {
		return function(record) {
			return record[key] != value;
		};
	}

	$scope.delRecord = function(table, record) {
		spModal.confirm("Delete this Record?").then(function(confirmed) {
			if (confirmed) {
				var ga = new GlideAjax('TableUtils');
				ga.addParam('sysparmname', 'deleteRecord')
				ga.addParam('sysparm_my_name', 'table,record')
				ga.getXML(callDelete);
			}
		})
	}

	function callDelete(res) {
		var answer = res.responseXML.documentElement.getAttribute("answer"); 
		alert(answer);
	}

	function getByIndex(record, index) {
		return record[Object.keys(record)[index]];
	}

	function deleteRecord(table, id) {
		spModal.confirm("Delete this Record?").then(function(confirmed) {
			if (!confirmed) return;

			$http.delete(
				'/api/x_naci_otr_ccr_dat/data/record', 
				{ params: {id: id, table: table} }
			).then(function(response) {
				var status = response.data.result;
				if (status)
					$scope.server.refresh();
				console.log(response);
			})
		})
	}


