sp_widget.script:
(function() {
data.admin = gs.getUser().isMemberOf('CCR_Database_Admin'); //data.admin = false;
var tableName = $sp.getParameter('table');
var headers = { 'x_naci_otr_ccr_dat_contact': [ 'sys_id', 'full_name', 'suffix', 'organization' ], 'x_naci_otr_ccr_dat_drug_development_collaborative': [ 'sys_id', 'project', 'ddc_meeting_date', 'primary_contact', 'ddc_funding' ], 'x_naci_otr_ccr_dat_inventions_patents': [ 'sys_id', 'title_of_inventions_patents', 'issued_patent_number', 'issue_date', 'nih_invention_number', 'patent_priority_date', 'contact_primary' ], 'x_naci_otr_ccr_dat_organization': [ 'sys_id', 'organization_name', 'type', 'phone_number' ], 'x_naci_otr_ccr_dat_outcome_type': [ 'sys_id', 'type' ], 'x_naci_otr_ccr_dat_partnerships': [ 'sys_id', 'type', 'project', 'technology_product', 'contact_primary', 'source_of_information' ], 'x_naci_otr_ccr_dat_project': [ 'sys_id', 'title', 'project_status', 'contact_primary', 'technologies_products' ], 'x_naci_otr_ccr_dat_tp_indication': [ 'sys_id', 'technologies_product', 'tp_site' ], 'x_naci_otr_ccr_dat_tp_outcome': [ 'sys_id', 'outcome_name', 'outcome_type', 'outcome_subtype', 'technologies_product', 't_p_indication', 'date_entered', 'date_of_outcome_event', 'source_of_information', 'comments' ], 'x_naci_otr_ccr_dat_tp_outcome_subtype': [ 'sys_id', 'type', 'subtype', 'value' ], 'x_naci_otr_ccr_dat_tp_site_type_look_up': [ 'sys_id', 'site', 'type', 'value' ], 'x_naci_otr_ccr_dat_tp_status': [ 'sys_id', 'technologies_product', 'tp_indication', 'status', 'source_information', 'date_entered' ], 'x_naci_otr_ccr_dat_technologies_products': [ 'sys_id', 'technology_product_name',
'organization', 'technology_type', 'regulatory_pathway', 'contact_primary', 'contact_secondary' ] };
data.tableName = tableName; data.label = TableUtils.getLabel(tableName); data.columns = TableUtils.getColumns(tableName, headers[tableName]).sort(function(a, b) { return headers[tableName].indexOf(a.name) - headers[tableName].indexOf(b.name); }); data.records = TableUtils.getRecords(tableName, data.columns);
})();
